DROP TABLE IF EXISTS `adm_metods_default-item`;

CREATE TABLE IF NOT EXISTS `adm_metods_default-item` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `id_module` varchar(255) NOT NULL,
  `metod` varchar(255) NOT NULL,
  `caption` varchar(255) NOT NULL,
  `properties` text,
  `flag_admin` tinyint(1) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `id_module` (`id_module`),
  KEY `metod` (`metod`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=569 ;

